import logo from './logo.svg';
import './App.css';
import picture1 from './pictures/monde.jpg';
import picture2 from './pictures/foret.jpg';
import picture3 from './pictures/encoreautre.jpg';
import picture4 from './pictures/montagne.jpg';
import picture5 from './pictures/otremontagne.jpg';
function App() {
  const [projects, setProjects] = useState([])
  const [images, setImages] = useState([])
  const [imageActive, setImageActive] = useState(0)
  const [imagesToMap, setImagesToMap] = useState([
    {
      url: picture1,
      id: 0
    },
    {
      url: picture2,
      id: 1
    },
    {
      url: picture3,
      id: 2
    },
    {
      url: picture4,
      id: 3
    },
    {
      url: picture5,
      id: 4
    },
  ])
  useEffect(() => {
    axios.get(`/projects`)
      .then(res => {
        setProjects(res.data)
      })
      .catch(error => {
        console.log(error);
      })
  }, [])
  useEffect(() => {
    axios.get(`/api/images`)
      .then(res => {
        setImages(res.data)
      })
      .catch(error => {
        console.log(error);
      })
  }, [])

  return (
    <div className="App">
      <div class="container">
        <div class="pannel active"
          style={{
            backgroundImage: "url(" + `${picture1}` + ")",
